import React from 'react';

class AddFishForm extends React.Component {
  createFish = event => {
    // Stop form from submitting
    event.preventDefault();
    console.log("making a dish!")

  };

  render() {
    return (
      <form className="fishEdit" onSubmit={this.createFish}>
        <input name="name" ref={this.name} type="text" placeholder="name" />
        <input name="price" ref={this.price} type="text" placeholder="price" />
        <select name="status" ref={this.status}>
          <option value="available">Fresh</option>
          <option value="unavailable">Sold Out</option>
        </select>
        <textarea name="desc" ref={this.desc} placeholder="desc" />
        <input name="image" ref={this.image} type="text" placeholder="image" />
        <button type="submit">Add Fish!</button>
      </form>
    );
  }
}

export default AddFishForm;
