import React from 'react'; // always need to import react into your components. this wont imput 40 copies, only one in the rendering
import { format } from 'util';

class StorePicker extends React.Component {
  render() {
    return (
      // you can only return ONE element from a return method. Hence these will be nested inside of a wrapper parent element. these can be wrapped inside a <React.Fragment>
    <React.Fragment>
      <p>Gish</p>
      <form action="" className="store-selector">
        <h2>Please enter a store</h2>
      </form>
    </React.Fragment>
    )
  }
}

export default StorePicker;
